const today = new Date();
const yesterday = new Date(today);
yesterday.setDate(yesterday.getDate() - 1);

function formatDate(date) {
  const year = date.getFullYear();
  const month = String(date.getMonth() + 1).padStart(2, '0');
  const day = String(date.getDate()).padStart(2, '0');
  return `${year}${month}${day}0400`;
}

const apiUrl = `https://export.arxiv.org/api/query?search_query=cat:cs.AI+AND+submittedDate:[${formatDate(yesterday)}+TO+${formatDate(today)}]&max_results=1000`;

return { json: { url: apiUrl } };

// n8n Code Node - ArXiv Email Template Generator
// This node expects input items with a 'data' array containing paper objects

const inputData = $input.all();

// Helper function to escape HTML
function escapeHtml(text) {
  if (!text) return '';
  return text
    .replace(/&/g, '&amp;')
    .replace(/</g, '&lt;')
    .replace(/>/g, '&gt;')
    .replace(/"/g, '&quot;')
    .replace(/'/g, '&#039;');
}

// Get current date
const today = new Date().toLocaleDateString('en-US', { 
  weekday: 'long', 
  year: 'numeric', 
  month: 'long', 
  day: 'numeric' 
});

// Process all input items
const results = inputData.map(item => {
  const data = item.json.data || [];

  // Separate successful papers from errors
  const papers = [];
  const summaryErrors = [];

  data.forEach(paper => {
    if (paper.output && paper.output.length > 0) {
      // Successfully processed paper
      papers.push({
        final_score: paper.relevance_score || 0,
        updated_key_insight: paper.key_insight || '',
        updated_expected_impact: paper.expected_impact || '',
        video_ideas: paper.video_ideas || '',
        summarized_paper: {
          ranked_paper: {
            paper: {
              title: paper.title || 'Untitled',
              pdf_link: paper.pdf_link || '#'
            }
          },
          summary_data: paper.output[0] || null
        }
      });
    } else {
      // Paper with processing error
      summaryErrors.push({
        ranked_paper: {
          paper: {
            title: paper.title || 'Untitled',
            pdf_link: paper.pdf_link || '#'
          },
          key_insight: paper.key_insight || '',
          expected_impact: paper.expected_impact || ''
        },
        error: 'Summary data could not be generated'
      });
    }
  });

  // Generate papers HTML
  let papersHtml = '';

  papers.forEach(paper => {
    papersHtml += `
          <tr>
            <td style="padding: 0 40px 30px;">
              <div style="border: 1px solid #e0e0e0; border-radius: 8px; overflow: hidden; background-color: #fafafa;">

                <!-- Paper Header -->
                <div style="background-color: #ffffff; padding: 24px; border-bottom: 3px solid #1155cc;">
                  <h2 style="margin: 0 0 12px; color: #1a1a1a; font-size: 20px; font-weight: 600; line-height: 1.4;">
                    ${escapeHtml(paper.summarized_paper.ranked_paper.paper.title)}
                  </h2>
                  <div style="margin-bottom: 12px;">
                    <span style="display: inline-block; background-color: #e3f2fd; color: #1976d2; font-size: 12px; font-weight: 600; padding: 6px 12px; border-radius: 4px; margin-right: 8px;">
                      ‚≠ê Final Score: ${paper.final_score}/10
                    </span>
                  </div>
                  <a href="${paper.summarized_paper.ranked_paper.paper.pdf_link}"
                    style="display: inline-block; color: #1155cc; text-decoration: none; font-size: 14px; font-weight: 500; padding: 8px 16px; border: 1px solid #1155cc; border-radius: 6px; transition: all 0.2s;">
                    üìÑ View PDF
                  </a>
                </div>

                <!-- Paper Content -->
                <div style="padding: 24px;">`;

    if (paper.updated_key_insight) {
      papersHtml += `
                  <div style="margin-bottom: 20px;">
                    <div style="display: inline-block; background-color: #1155cc; color: #ffffff; font-size: 11px; font-weight: 600; text-transform: uppercase; letter-spacing: 0.5px; padding: 4px 10px; border-radius: 4px; margin-bottom: 8px;">
                      üí° Key Insight
                    </div>
                    <p style="margin: 8px 0 0; color: #333333; font-size: 15px; line-height: 1.6;">
                      ${escapeHtml(paper.updated_key_insight)}
                    </p>
                  </div>`;
    }

    if (paper.updated_expected_impact) {
      papersHtml += `
                  <div style="margin-bottom: 20px;">
                    <div style="display: inline-block; background-color: #1155cc; color: #ffffff; font-size: 11px; font-weight: 600; text-transform: uppercase; letter-spacing: 0.5px; padding: 4px 10px; border-radius: 4px; margin-bottom: 8px;">
                      üéØ Expected Impact
                    </div>
                    <p style="margin: 8px 0 0; color: #333333; font-size: 15px; line-height: 1.6;">
                      ${escapeHtml(paper.updated_expected_impact)}
                    </p>
                  </div>`;
    }

    if (paper.video_ideas) {
      papersHtml += `
                  <div style="margin-bottom: 20px;">
                    <div style="display: inline-block; background-color: #1155cc; color: #ffffff; font-size: 11px; font-weight: 600; text-transform: uppercase; letter-spacing: 0.5px; padding: 4px 10px; border-radius: 4px; margin-bottom: 8px;">
                      üé¨ Video Ideas
                    </div>
                    <p style="margin: 8px 0 0; color: #333333; font-size: 15px; line-height: 1.6; white-space: pre-line;">
                      ${escapeHtml(paper.video_ideas)}
                    </p>
                  </div>`;
    }

    if (paper.summarized_paper.summary_data) {
      const summary = paper.summarized_paper.summary_data;
      papersHtml += `
                  <div style="background-color: #f0f4ff; border-left: 4px solid #1155cc; padding: 16px; border-radius: 4px;">
                    <div style="display: inline-block; background-color: #1155cc; color: #ffffff; font-size: 11px; font-weight: 600; text-transform: uppercase; letter-spacing: 0.5px; padding: 4px 10px; border-radius: 4px; margin-bottom: 8px;">
                      üìã Summary
                    </div>

                    <div style="margin-top: 12px;">
                      <strong style="color: #1155cc; font-size: 13px;">Approach:</strong>
                      <p style="margin: 4px 0 12px; color: #2c3e50; font-size: 14px; line-height: 1.6;">
                        ${escapeHtml(summary.approach)}
                      </p>

                      <strong style="color: #1155cc; font-size: 13px;">Key Findings:</strong>
                      <p style="margin: 4px 0 12px; color: #2c3e50; font-size: 14px; line-height: 1.6;">
                        ${escapeHtml(summary.key_findings)}
                      </p>

                      <strong style="color: #1155cc; font-size: 13px;">Value:</strong>
                      <p style="margin: 4px 0 12px; color: #2c3e50; font-size: 14px; line-height: 1.6;">
                        ${escapeHtml(summary.value)}
                      </p>

                      <strong style="color: #1155cc; font-size: 13px;">Limitations:</strong>
                      <p style="margin: 4px 0 12px; color: #2c3e50; font-size: 14px; line-height: 1.6;">
                        ${escapeHtml(summary.limitations)}
                      </p>

                      <strong style="color: #1155cc; font-size: 13px;">Bottom Line:</strong>
                      <p style="margin: 4px 0 0; color: #2c3e50; font-size: 14px; line-height: 1.6;">
                        ${escapeHtml(summary.bottom_line)}
                      </p>
                    </div>
                  </div>`;
    }

    papersHtml += `
                </div>
              </div>
            </td>
          </tr>`;
  });

  // Generate error papers HTML
  let errorsHtml = '';

  if (summaryErrors.length > 0) {
    errorsHtml = `
          <tr>
            <td style="padding: 0 40px 30px;">
              <div style="border: 2px solid #f57c00; border-radius: 8px; overflow: hidden; background-color: #fff3e0;">
                <div style="background-color: #ff9800; padding: 16px; text-align: center;">
                  <h3 style="margin: 0; color: #ffffff; font-size: 18px; font-weight: 600;">
                    ‚ö†Ô∏è Papers with Processing Errors
                  </h3>
                </div>
                <div style="padding: 20px;">
                  <p style="margin: 0 0 16px; color: #e65100; font-size: 14px; line-height: 1.6;">
                    The following papers could not be fully processed. Basic information is provided below:
                  </p>
                </div>
              </div>
            </td>
          </tr>`;

    summaryErrors.forEach(errorItem => {
      errorsHtml += `
          <tr>
            <td style="padding: 0 40px 30px;">
              <div style="border: 1px solid #ffb74d; border-radius: 8px; overflow: hidden; background-color: #fff8e1;">

                <!-- Error Paper Header -->
                <div style="background-color: #ffffff; padding: 24px; border-bottom: 3px solid #ff9800; border-left: 4px solid #f57c00;">
                  <h2 style="margin: 0 0 12px; color: #1a1a1a; font-size: 20px; font-weight: 600; line-height: 1.4;">
                    ${escapeHtml(errorItem.ranked_paper.paper.title)}
                  </h2>
                  <a href="${errorItem.ranked_paper.paper.pdf_link}"
                    style="display: inline-block; color: #f57c00; text-decoration: none; font-size: 14px; font-weight: 500; padding: 8px 16px; border: 1px solid #f57c00; border-radius: 6px; transition: all 0.2s;">
                    üìÑ View PDF
                  </a>
                </div>

                <!-- Error Paper Content -->
                <div style="padding: 24px;">`;

      if (errorItem.ranked_paper.key_insight) {
        errorsHtml += `
                  <div style="margin-bottom: 20px;">
                    <div style="display: inline-block; background-color: #ff9800; color: #ffffff; font-size: 11px; font-weight: 600; text-transform: uppercase; letter-spacing: 0.5px; padding: 4px 10px; border-radius: 4px; margin-bottom: 8px;">
                      üí° Key Insight
                    </div>
                    <p style="margin: 8px 0 0; color: #333333; font-size: 15px; line-height: 1.6;">
                      ${escapeHtml(errorItem.ranked_paper.key_insight)}
                    </p>
                  </div>`;
      }

      if (errorItem.ranked_paper.expected_impact) {
        errorsHtml += `
                  <div style="margin-bottom: 20px;">
                    <div style="display: inline-block; background-color: #ff9800; color: #ffffff; font-size: 11px; font-weight: 600; text-transform: uppercase; letter-spacing: 0.5px; padding: 4px 10px; border-radius: 4px; margin-bottom: 8px;">
                      üéØ Expected Impact
                    </div>
                    <p style="margin: 8px 0 0; color: #333333; font-size: 15px; line-height: 1.6;">
                      ${escapeHtml(errorItem.ranked_paper.expected_impact)}
                    </p>
                  </div>`;
      }

      errorsHtml += `
                  <!-- Error Message -->
                  <div style="background-color: #ffebee; border-left: 4px solid #d32f2f; padding: 16px; border-radius: 4px; margin-top: 20px;">
                    <div style="display: inline-block; background-color: #d32f2f; color: #ffffff; font-size: 11px; font-weight: 600; text-transform: uppercase; letter-spacing: 0.5px; padding: 4px 10px; border-radius: 4px; margin-bottom: 8px;">
                      ‚ùå Error Details
                    </div>
                    <p style="margin: 8px 0 0; color: #c62828; font-size: 14px; line-height: 1.6; font-family: 'Courier New', monospace;">
                      ${escapeHtml(errorItem.error)}
                    </p>
                  </div>

                </div>
              </div>
            </td>
          </tr>`;
    });
  }

  // Complete HTML template
  const html = `<!DOCTYPE html>
<html lang="en">

<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>ArXiv Paper Report</title>
</head>

<body style="margin: 0; padding: 0; font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif; background-color: #f5f5f5;">
  <table role="presentation" style="width: 100%; border-collapse: collapse;">
    <tr>
      <td style="padding: 40px 20px;">
        <table role="presentation" style="max-width: 680px; margin: 0 auto; background-color: #ffffff; border-radius: 12px; box-shadow: 0 2px 8px rgba(0,0,0,0.08); overflow: hidden;">

          <!-- Header -->
          <tr>
            <td style="background: linear-gradient(135deg, #1155cc 0%, #0d47a1 100%); padding: 40px 40px 30px; text-align: center;">
              <h1 style="margin: 0; color: #ffffff; font-size: 28px; font-weight: 600; letter-spacing: -0.5px;">
                üìö Your Daily ArXiv Report presented by @OfficeOptOut
              </h1>
              <p style="margin: 10px 0 0; color: rgba(255,255,255,0.9); font-size: 14px;">
                ${today}
              </p>
            </td>
          </tr>

          <!-- Introduction -->
          <tr>
            <td style="padding: 30px 40px 20px;">
              <p style="margin: 0; color: #333333; font-size: 16px; line-height: 1.6;">
                Here are today's most relevant papers for your content strategy:
              </p>
            </td>
          </tr>

          <!-- Papers Loop -->
          ${papersHtml}

          <!-- Summary Errors Section -->
          ${errorsHtml}

          <!-- Footer -->
          <tr>
            <td style="padding: 30px 40px 40px; text-align: center; border-top: 1px solid #e0e0e0;">
              <p style="margin: 0 0 10px; color: #666666; font-size: 13px; line-height: 1.5;">
                This automated report was generated by your ArXiv Content Scraper
              </p>
              <p style="margin: 0; color: #999999; font-size: 12px;">
                Keep creating amazing content! üöÄ
              </p>
            </td>
          </tr>

        </table>
      </td>
    </tr>
  </table>
</body>

</html>`;

  return {
    json: {
      html: html,
      paperCount: papers.length,
      errorCount: summaryErrors.length,
      date: today
    }
  };
});

return results

parameters.pythonCode:
cleaned_items = []

for item in _items:
    entries = item["json"].get("feed", {}).get("entry", [])

    # Keƒè je len 1 z√°znam, XML node niekedy d√° dict namiesto listu
    if isinstance(entries, dict):
        entries = [entries]

    for entry in entries:
        links = entry.get("link", [])
        if isinstance(links, dict):
            links = [links]

        pdf_link = None
        for possible_link in links:
            href = possible_link.get("href", "")
            if possible_link.get("type") == "application/pdf" and href:
                pdf_link = href
                break
            if "pdf" in href:
                pdf_link = href
                break

        cleaned_items.append({
            "title": entry.get("title", ""),
            "abstract": entry.get("summary", ""),
            "link": entry.get("id", ""),
            "pdf_link": pdf_link,
        })

# Code node m√° vr√°ti≈• list items
return [{"json": {"entries": cleaned_items}}]
